package main

import (
	"github.com/iain17/decentralizer/app"
	"github.com/iain17/logger"
	"github.com/iain17/decentralizer/serve"
)

//This is the privatekey
const ourNetwork = ""

func init() {
	logger.AddOutput(logger.Stdout{
		MinLevel: logger.INFO, //logger.DEBUG,
		Colored:  true,
	})
	//logging.Configure(logging.LevelDebug)
}

func main() {
	app, err := app.New(ourNetwork)
	if err != nil {
		panic(err)
	}

	s := serve.New(app)
	s.ListenTCP(3036)

	select{}
}